import React, { useState } from 'react'

const Header = (props) => {

  return (<h1>{props.course}</h1>)
}
const Part = (props) => {
  return (
    <p>
      {props.type} {props.number}
    </p>
  )
}
const Content = (props) => {

  return (
    <div>
      <Part type={"Good"} exercises={props.parts[0].exercises} />
      <Part type={props.name} exercises={props.parts[1].exercises} />
      <Part type={props.name} exercises={props.parts[2].exercises} />
    </div>
  )

}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGoodClick = () => {
    const newGood = good + 1
    setGood(newGood)
  }

  const handleNeutralClick = () => {
    const newNeutral = neutral + 1
    setNeutral(newNeutral)
  }

  const handleBadClick = () => {
    const newBad = bad + 1
    setBad(newBad)
  }

  return (
    <div>
      <Header course={"get feedback"}/>

      <button onClick={handleGoodClick}>good</button>

      <button onClick={handleNeutralClick}>neutral</button>

      <button onClick={handleBadClick}>bad</button>

      <Header course={"statistics"}/>
      {"Good: " }{good}
      {"Neutral: "} {neutral}
      {bad}
    </div>
  )
}

export default App
