import React, { useState } from 'react'

const Header = (props) => {

  return (<h1>{props.course}</h1>)
}
const Part = (props) => {
  return (
    <p>
      {props.type} {props.number}
    </p>
  )
}
const Content = (props) => {

  return (
        <div>
            <Part type={props.display.buttons[0].name} number={props.display.buttons[0].data} />
            <Part type={props.display.buttons[1].name} number={props.display.buttons[1].data} />
            <Part type={props.display.buttons[2].name} number={props.display.buttons[2].data} />
        </div>

  )

}

const Statistics = (props) => {
    if(props.display.stats[0].data===0) {
        return(<p>'No feedback given'</p>)
    }
    return(
        <div>
            <Part type={props.display.stats[0].name} number={props.display.stats[0].data} />
            <Part type={props.display.stats[1].name} number={props.display.stats[1].data} />
            <Part type={props.display.stats[2].name} number={props.display.stats[2].data} />
        </div>
    )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const clickData = {
      buttons:[
          {
              name:'Good:',
              data: good
          },
          {
              name:'Neutral: ',
              data: neutral
          },
          {
              name:'Bad: ',
              data: bad
          }
      ]
  }
  const statData = {
      stats:[
          {
              name:'All: ',
              data: good+neutral+bad
          },
          {
              name:'Average: ',
              data: (good+(-1*bad))/9
          },
          {
              name:'Positive: ',
              data:( good/(good+neutral+bad) + ' %')
          }
      ]
  }

  const handleGoodClick = () => {
    const newGood = good + 1
    setGood(newGood)
  }

  const handleNeutralClick = () => {
    const newNeutral = neutral + 1
    setNeutral(newNeutral)
  }

  const handleBadClick = () => {
    const newBad = bad + 1
    setBad(newBad)
  }

  return (
    <div>
        <Header course={"get feedback"} />

        <button onClick={handleGoodClick}>good</button>

        <button onClick={handleNeutralClick}>neutral</button>

        <button onClick={handleBadClick}>bad</button>

        <Header course={"statistics"} />

        <Content display={clickData} />

        <Statistics display={statData} />

    </div>
  )
}

export default App
